	LEFT OUTER JOIN CaseDetail AS CD
	ON SMT.RecId =CD.ServiceId 
	AND CD.CYear =@CYear 
	AND ((CMonth >=@CMonth AND CMonth <=@EMonth) OR @CMonth+@EMonth=0)
