	--AND CD.StateId =@StateId	 
	AND CD.CompanyId =@CompanyId
	GROUP BY CD.ServiceId ,SMT.RecId,SMT.ServiceName,SMT.PServiceName 
	ORDER BY SMT.RecId
END
