	END	
	DECLARE @DayDate as datetime
	SELECT @DayDate= convert(nvarchar(4),@CYear) +'-'+ convert(nvarchar(4),@CMonth) + '-1'
	Declare @NoOfDay as int
	SELECT @NoOfDay=Day(DATEADD(day,-1,DATEADD(month,1,@DayDate)))

	SELECT SM.RecId AS OrderId
