     FROM Dental AS D
     WHERE (D.CompanyId = @CompanyId)
	 --AND(D.StateId = @StateId)
	 --And (D.Machine = @Machine)
	 AND(D.CMonth = @CMonth)
	 AND(D.CYear = @CYear)
	 AND(D.Htype = @Htype)
	 
