	WHERE (SM.ParentId = 0)
	AND (SM.IsActive = 1)
	AND SM.GroupId=@GroupId
	AND (SM.ServiceName like @SearchVal + '%' OR @SearchVal='')
	
	UNION ALL
	
	SELECT (CASE WHEN SM.ParentId = 0 THEN SM.RecId ELSE SM.ParentId END) AS OrderId
		  ,SM.RecId AS SMRecId
		  ,SM.ParentId AS SMParentId
		  ,SM.ServiceName
		  ,CD.RecId AS CDRecId
		  ,CD.ServiceId AS CDServiceId
		  ,CD.Gender
		  ,CD.CMonth
		  ,CD.CYear
